import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';

// Simple navigation component
const Navigation = ({ currentPage, setCurrentPage }) => (
  <div style={{
    background: 'rgba(26,29,41,0.95)',
    padding: '20px',
    borderBottom: '1px solid rgba(255,255,255,0.1)',
    display: 'flex',
    gap: '20px',
    alignItems: 'center'
  }}>
    <h2 style={{ margin: 0, color: '#667eea' }}>üß† MindfulCampus</h2>
    <nav style={{ display: 'flex', gap: '15px' }}>
      {['Dashboard', 'Analysis', 'Support', 'Insights'].map(page => (
        <button
          key={page}
          onClick={() => setCurrentPage(page)}
          style={{
            background: currentPage === page ? '#667eea' : 'transparent',
            color: 'white',
            border: '1px solid #667eea',
            padding: '8px 16px',
            borderRadius: '6px',
            cursor: 'pointer'
          }}
        >
          {page}
        </button>
      ))}
    </nav>
  </div>
);

// Page components
const DashboardPage = () => (
  <div style={{ padding: '40px', textAlign: 'center' }}>
    <h1>üè† Dashboard</h1>
    <p>Student mental wellness overview and daily check-ins</p>
    <div style={{ 
      display: 'grid', 
      gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))', 
      gap: '20px',
      maxWidth: '800px',
      margin: '30px auto'
    }}>
      {[
        { title: 'Mood Score', value: '7.2/10', icon: 'üòä' },
        { title: 'Wellness Streak', value: '5 days', icon: 'üî•' },
        { title: 'Active Support', value: '12 peers', icon: 'ü§ù' },
        { title: 'Interventions', value: '3 today', icon: 'üßò‚Äç‚ôÄÔ∏è' }
      ].map((stat, i) => (
        <div key={i} style={{
          background: 'rgba(255,255,255,0.1)',
          padding: '20px',
          borderRadius: '12px',
          border: '1px solid rgba(255,255,255,0.1)'
        }}>
          <div style={{ fontSize: '2rem', marginBottom: '10px' }}>{stat.icon}</div>
          <h3 style={{ margin: '10px 0', color: '#667eea' }}>{stat.value}</h3>
          <p style={{ margin: 0, opacity: 0.8 }}>{stat.title}</p>
        </div>
      ))}
    </div>
  </div>
);

const AnalysisPage = () => (
  <div style={{ padding: '40px' }}>
    <h1>üß† Emotional Analysis</h1>
    <div style={{ maxWidth: '600px', margin: '0 auto' }}>
      <textarea
        placeholder="Type how you're feeling... (e.g., 'I'm stressed about my finals next week')"
        style={{
          width: '100%',
          height: '120px',
          padding: '15px',
          borderRadius: '8px',
          border: '1px solid rgba(255,255,255,0.3)',
          background: 'rgba(255,255,255,0.1)',
          color: 'white',
          fontSize: '16px',
          marginBottom: '20px'
        }}
      />
      <button style={{
        background: '#667eea',
        color: 'white',
        border: 'none',
        padding: '12px 24px',
        borderRadius: '8px',
        cursor: 'pointer',
        fontSize: '16px'
      }}>
        üéØ Analyze Emotion
      </button>

      <div style={{
        marginTop: '30px',
        padding: '20px',
        background: 'rgba(102,126,234,0.1)',
        border: '1px solid rgba(102,126,234,0.3)',
        borderRadius: '8px'
      }}>
        <h3>Demo Result: Sentiment Analysis Ready! üöÄ</h3>
        <p>AI can detect emotions like stress, happiness, anxiety, and more from text input.</p>
      </div>
    </div>
  </div>
);

const SupportPage = () => (
  <div style={{ padding: '40px' }}>
    <h1>ü§ù Peer Support Network</h1>
    <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '30px', maxWidth: '1000px', margin: '0 auto' }}>
      <div>
        <h3>Your Support Connections</h3>
        {['Anonymous Student A', 'Anonymous Student B', 'Anonymous Student C'].map((peer, i) => (
          <div key={i} style={{
            background: 'rgba(255,255,255,0.1)',
            padding: '15px',
