import React, { useState, useEffect } from 'react';
import {
  Container,
  Grid,
  Card,
  CardContent,
  Typography,
  Box,
  Button,
  TextField,
  Chip,
  Alert,
  CircularProgress,
  Slider,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Timeline,
  TimelineItem,
  TimelineContent,
  TimelineDot,
  TimelineSeparator,
  TimelineConnector,
  Paper,
  Avatar
} from '@mui/material';
import {
  Psychology,
  SentimentSatisfied,
  SentimentNeutral,
  SentimentDissatisfied,
  TrendingUp,
  Assessment,
  CloudUpload,
  Facebook,
  Instagram,
  Twitter,
  Timeline as TimelineIcon
} from '@mui/icons-material';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer, RadarChart, PolarGrid, PolarAngleAxis, PolarRadiusAxis, Radar, Legend } from 'recharts';
import { useEmotion } from '../context/EmotionContext';
import { analyzeSentiment, analyzeTypingPatterns } from '../services/aiService';

const EmotionalAnalysis = () => {
  const {
    currentMood,
    moodHistory,
    socialMediaActivity,
    analyzeMood,
    addSocialActivity,
    isAnalyzing
  } = useEmotion();

  const [analysisText, setAnalysisText] = useState('');
  const [analysisResult, setAnalysisResult] = useState(null);
  const [selectedPlatform, setSelectedPlatform] = useState('general');
  const [emotionalIntensity, setEmotionalIntensity] = useState(5);
  const [typingData, setTypingData] = useState([]);
  const [isTypingAnalysis, setIsTypingAnalysis] = useState(false);

  const platforms = [
    { value: 'general', label: 'General Text', icon: 'üìù' },
    { value: 'instagram', label: 'Instagram', icon: 'üì∑' },
    { value: 'twitter', label: 'Twitter', icon: 'üê¶' },
    { value: 'facebook', label: 'Facebook', icon: 'üë•' },
    { value: 'discord', label: 'Discord', icon: 'üéÆ' }
  ];

  // Simulate typing pattern tracking
  useEffect(() => {
    let keystrokes = [];
    let lastKeystroke = Date.now();

    const handleKeydown = (e) => {
      const now = Date.now();
      const timeDiff = now - lastKeystroke;

      keystrokes.push({
        key: e.key,
        timestamp: now,
        pause: timeDiff,
        speed: timeDiff > 0 ? 1000 / timeDiff : 0
      });

      // Keep only recent keystrokes
      if (keystrokes.length > 50) {
        keystrokes = keystrokes.slice(-50);
      }

      setTypingData([...keystrokes]);
      lastKeystroke = now;
    };

    if (isTypingAnalysis) {
      document.addEventListener('keydown', handleKeydown);
      return () => document.removeEventListener('keydown', handleKeydown);
    }
  }, [isTypingAnalysis]);

  const handleAnalyze = async () => {
    if (!analysisText.trim()) return;

    try {
      const result = await analyzeMood(analysisText);
      setAnalysisResult(result);

      // Add to social media activity if platform selected
      if (selectedPlatform !== 'general') {
        addSocialActivity({
          platform: selectedPlatform,
          content: analysisText.substring(0, 100) + '...',
          sentiment: result.label.toLowerCase(),
          confidence: result.confidence,
          intensity: emotionalIntensity
        });
      }
    } catch (error) {
      console.error('Analysis failed:', error);
    }
  };

  const getEmotionIcon = (emotion) => {
    const icons = {
      positive: <SentimentSatisfied sx={{ color: '#4caf50' }} />,
      neutral: <SentimentNeutral sx={{ color: '#ff9800' }} />,
      negative: <SentimentDissatisfied sx={{ color: '#f44336' }} />,
      stressed: <Psychology sx={{ color: '#e91e63' }} />
    };
    return icons[emotion.toLowerCase()] || <SentimentNeutral />;
  };

  const getConfidenceColor = (confidence) => {
    if (confidence > 0.8) return '#4caf50';
    if (confidence > 0.6) return '#ff9800';
    return '#f44336';
  };

  // Prepare data for emotion pattern analysis
  const emotionPatternData = [
    { emotion: 'Joy', current: 75, average: 60 },
    { emotion: 'Sadness', current: 20, average: 25 },
    { emotion: 'Anger', current: 15, average: 20 },
    { emotion: 'Fear', current: 30, average: 35 },
    { emotion: 'Surprise', current: 65, average: 50 },
    { emotion: 'Disgust', current: 10, average: 15 }
  ];

  // Prepare timeline data
  const timelineData = moodHistory.slice(-10).reverse();

  // Mock social media trigger analysis
  const triggerAnalysis = [
    { trigger: 'Study Stress', frequency: 45, impact: 'High' },
    { trigger: 'Social Comparison', frequency: 32, impact: 'Medium' },
    { trigger: 'News Content', frequency: 28, impact: 'High' },
    { trigger: 'Relationship Posts', frequency: 22, impact: 'Medium' },
    { trigger: 'Academic Pressure', frequency: 38, impact: 'High' }
  ];

  return (
    <Container maxWidth="lg" sx={{ py: 4 }}>
      <Typography variant="h4" fontWeight="bold" gutterBottom>
        Emotional Analysis Lab üß†
      </Typography>
      <Typography variant="body1" color="text.secondary" sx={{ mb: 4 }}>
        Analyze your emotions with AI-powered insights and discover patterns in your mental wellness journey
      </Typography>

      <Grid container spacing={3}>
        {/* Text Analysis Section */}
        <Grid item xs={12} md={8}>
          <Card>
            <CardContent>
              <Typography variant="h6" gutterBottom sx={{ display: 'flex', alignItems: 'center' }}>
                <Psychology sx={{ mr: 1 }} />
                Real-time Emotion Analysis
              </Typography>

              <Box sx={{ mb: 3 }}>
                <FormControl fullWidth sx={{ mb: 2 }}>
                  <InputLabel>Platform/Context</InputLabel>
                  <Select
                    value={selectedPlatform}
                    label="Platform/Context"
                    onChange={(e) => setSelectedPlatform(e.target.value)}
                  >
                    {platforms.map((platform) => (
                      <MenuItem key={platform.value} value={platform.value}>
                        <Box sx={{ display: 'flex', alignItems: 'center' }}>
                          <span style={{ marginRight: 8 }}>{platform.icon}</span>
                          {platform.label}
                        </Box>
                      </MenuItem>
                    ))}
                  </Select>
                </FormControl>

                <TextField
                  fullWidth
                  multiline
                  rows={4}
                  value={analysisText}
                  onChange={(e) => setAnalysisText(e.target.value)}
                  placeholder="Share what's on your mind... How are you feeling about today? What happened recently?"
                  variant="outlined"
                  sx={{ mb: 2 }}
                />

                <Box sx={{ mb: 2 }}>
                  <Typography gutterBottom>Emotional Intensity</Typography>
                  <Slider
                    value={emotionalIntensity}
                    onChange={(e, value) => setEmotionalIntensity(value)}
                    min={1}
                    max={10}
                    marks
                    valueLabelDisplay="auto"
                    sx={{ color: '#667eea' }}
                  />
                </Box>

                <Box sx={{ display: 'flex', gap: 2, alignItems: 'center' }}>
                  <Button
                    variant="contained"
                    onClick={handleAnalyze}
                    disabled={isAnalyzing || !analysisText.trim()}
                    startIcon={isAnalyzing ? <CircularProgress size={20} /> : <Assessment />}
                    sx={{ flexGrow: 1 }}
                  >
                    {isAnalyzing ? 'Analyzing...' : 'Analyze Emotions'}
                  </Button>

                  <Button
                    variant="outlined"
                    onClick={() => setIsTypingAnalysis(!isTypingAnalysis)}
                    color={isTypingAnalysis ? 'secondary' : 'primary'}
                  >
                    {isTypingAnalysis ? 'Stop' : 'Start'} Typing Analysis
                  </Button>
                </Box>
              </Box>

              {/* Analysis Results */}
              {analysisResult && (
                <Alert 
                  severity="info" 
                  sx={{ 
                    mb: 2,
                    background: 'rgba(102,126,234,0.1)',
                    border: '1px solid rgba(102,126,234,0.3)'
                  }}
                >
                  <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
                    <Box sx={{ display: 'flex', alignItems: 'center' }}>
                      {getEmotionIcon(analysisResult.label)}
                      <Box sx={{ ml: 2 }}>
                        <Typography variant="subtitle2" fontWeight="600">
                          Detected Emotion: {analysisResult.label}
                        </Typography>
                        <Typography variant="caption">
                          Confidence: {(analysisResult.confidence * 100).toFixed(1)}%
                        </Typography>
                      </Box>
                    </Box>
                    <Chip
                      label={analysisResult.label}
                      sx={{
                        backgroundColor: getConfidenceColor(analysisResult.confidence),
                        color: 'white',
                        fontWeight: 600
                      }}
                    />
                  </Box>
                </Alert>
              )}

              {/* Typing Pattern Analysis */}
              {isTypingAnalysis && (
                <Paper sx={{ p: 2, mb: 2, background: 'rgba(255,255,255,0.05)' }}>
                  <Typography variant="subtitle2" gutterBottom>
                    Live Typing Pattern Analysis
                  </Typography>
                  <Box sx={{ display: 'flex', gap: 2, flexWrap: 'wrap' }}>
                    <Chip 
                      label={`Keystrokes: ${typingData.length}`} 
                      size="small" 
                      variant="outlined" 
                    />
                    <Chip 
                      label={`Avg Speed: ${typingData.length > 0 ? (typingData.reduce((sum, k) => sum + (k.speed || 0), 0) / typingData.length).toFixed(1) : 0} WPM`}
                      size="small" 
                      variant="outlined" 
                    />
                    <Chip 
                      label={`Long Pauses: ${typingData.filter(k => k.pause > 2000).length}`}
                      size="small" 
                      variant="outlined" 
                    />
                  </Box>
                </Paper>
              )}
            </CardContent>
          </Card>
        </Grid>

        {/* Current Mood Status */}
        <Grid item xs={12} md={4}>
          <Card sx={{ mb: 3 }}>
            <CardContent sx={{ textAlign: 'center' }}>
              <Typography variant="h6" gutterBottom>
                Current Mood Status
              </Typography>
              <Box sx={{ fontSize: '4rem', mb: 2 }}>
                {currentMood === 'positive' ? 'üòä' : 
                 currentMood === 'neutral' ? 'üòê' : 
                 currentMood === 'negative' ? 'üòû' : 
                 currentMood === 'stressed' ? 'üò∞' : 'ü§î'}
              </Box>
              <Typography variant="h5" fontWeight="600" sx={{ textTransform: 'capitalize' }}>
                {currentMood || 'Unknown'}
              </Typography>
              <Typography variant="body2" color="text.secondary">
                Last updated: {new Date().toLocaleTimeString()}
              </Typography>
            </CardContent>
          </Card>

          {/* Mood Timeline */}
          <Card>
            <CardContent>
              <Typography variant="h6" gutterBottom sx={{ display: 'flex', alignItems: 'center' }}>
                <TimelineIcon sx={{ mr: 1 }} />
                Recent Mood Timeline
              </Typography>
              <Timeline>
                {timelineData.slice(0, 5).map((entry, index) => (
                  <TimelineItem key={index}>
                    <TimelineSeparator>
                      <TimelineDot sx={{ 
                        bgcolor: entry.mood === 'positive' ? '#4caf50' : 
                                entry.mood === 'neutral' ? '#ff9800' : 
                                entry.mood === 'negative' ? '#f44336' : '#e91e63'
                      }}>
                        {entry.mood === 'positive' ? 'üòä' : 
                         entry.mood === 'neutral' ? 'üòê' : 
                         entry.mood === 'negative' ? 'üòû' : 'üò∞'}
                      </TimelineDot>
                      {index < timelineData.length - 1 && <TimelineConnector />}
                    </TimelineSeparator>
                    <TimelineContent>
                      <Typography variant="body2" sx={{ textTransform: 'capitalize' }}>
                        {entry.mood}
                      </Typography>
                      <Typography variant="caption" color="text.secondary">
                        {new Date(entry.timestamp).toLocaleString()}
                      </Typography>
                    </TimelineContent>
                  </TimelineItem>
                ))}
              </Timeline>
            </CardContent>
          </Card>
        </Grid>

        {/* Emotion Pattern Radar Chart */}
        <Grid item xs={12} md={6}>
          <Card>
            <CardContent>
              <Typography variant="h6" gutterBottom>
                Emotion Pattern Analysis
              </Typography>
              <Box sx={{ height: 300 }}>
                <ResponsiveContainer width="100%" height="100%">
                  <RadarChart data={emotionPatternData}>
                    <PolarGrid />
                    <PolarAngleAxis dataKey="emotion" />
                    <PolarRadiusAxis angle={90} domain={[0, 100]} />
                    <Radar
                      name="Current"
                      dataKey="current"
                      stroke="#667eea"
                      fill="#667eea"
                      fillOpacity={0.3}
                      strokeWidth={2}
                    />
                    <Radar
                      name="Average"
                      dataKey="average"
                      stroke="#764ba2"
                      fill="#764ba2"
                      fillOpacity={0.1}
                      strokeWidth={1}
                      strokeDasharray="5 5"
                    />
                    <Legend />
                  </RadarChart>
                </ResponsiveContainer>
              </Box>
            </CardContent>
          </Card>
        </Grid>

        {/* Trigger Analysis */}
        <Grid item xs={12} md={6}>
          <Card>
            <CardContent>
              <Typography variant="h6" gutterBottom>
                Emotional Triggers Analysis
              </Typography>
              <Box sx={{ height: 300 }}>
                <ResponsiveContainer width="100%" height="100%">
                  <BarChart data={triggerAnalysis} layout="horizontal">
                    <CartesianGrid strokeDasharray="3 3" />
                    <XAxis type="number" />
                    <YAxis dataKey="trigger" type="category" width={120} />
                    <Tooltip />
                    <Bar dataKey="frequency" fill="#667eea" />
                  </BarChart>
                </ResponsiveContainer>
              </Box>
            </CardContent>
          </Card>
        </Grid>

        {/* Social Media Activity Log */}
        <Grid item xs={12}>
          <Card>
            <CardContent>
              <Typography variant="h6" gutterBottom>
                Recent Social Media Emotional Impact
              </Typography>
              <Box sx={{ maxHeight: 400, overflowY: 'auto' }}>
                {socialMediaActivity.length > 0 ? (
                  socialMediaActivity.slice(0, 10).map((activity, index) => (
                    <Box
                      key={activity.id}
                      sx={{
                        p: 2,
                        mb: 1,
                        borderRadius: 2,
                        background: 'rgba(255,255,255,0.05)',
                        border: '1px solid rgba(255,255,255,0.1)'
                      }}
                    >
                      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
                        <Box sx={{ display: 'flex', alignItems: 'center' }}>
                          <Avatar sx={{ mr: 2, width: 32, height: 32 }}>
                            {platforms.find(p => p.value === activity.platform)?.icon}
                          </Avatar>
                          <Box>
                            <Typography variant="subtitle2">
                              {activity.platform.charAt(0).toUpperCase() + activity.platform.slice(1)}
                            </Typography>
                            <Typography variant="body2" color="text.secondary">
                              {activity.content}
                            </Typography>
                          </Box>
                        </Box>
                        <Box sx={{ textAlign: 'right' }}>
                          <Chip
                            label={activity.sentiment}
                            size="small"
                            sx={{
                              backgroundColor: activity.sentiment === 'positive' ? '#4caf50' : 
                                             activity.sentiment === 'neutral' ? '#ff9800' : '#f44336',
                              color: 'white',
                              mb: 1
                            }}
                          />
                          <Typography variant="caption" color="text.secondary" sx={{ display: 'block' }}>
                            {new Date(activity.timestamp).toLocaleString()}
                          </Typography>
                        </Box>
                      </Box>
                    </Box>
                  ))
                ) : (
                  <Typography variant="body2" color="text.secondary" sx={{ textAlign: 'center', py: 4 }}>
                    No social media activity tracked yet. Start analyzing your posts to see emotional patterns!
                  </Typography>
                )}
              </Box>
            </CardContent>
          </Card>
        </Grid>
      </Grid>
    </Container>
  );
};

export default EmotionalAnalysis;
