import React, { useState, useEffect } from 'react';
import Dashboard from './components/Dashboard';
import EmotionalAnalysis from './components/EmotionalAnalysis';

// Navigation component (same as before)
const Navigation = ({ currentPage, setCurrentPage }) => (
  <div style={{
    background: 'rgba(26,29,41,0.95)',
    padding: '20px',
    borderBottom: '1px solid rgba(255,255,255,0.1)',
    display: 'flex',
    gap: '20px',
    alignItems: 'center'
  }}>
    <h2 style={{ margin: 0, color: '#667eea' }}>🧠 MindfulCampus</h2>
    <nav style={{ display: 'flex', gap: '15px' }}>
      {['Dashboard', 'Analysis', 'Support', 'Insights'].map(page => (
        <button
          key={page}
          onClick={() => setCurrentPage(page)}
          style={{
            background: currentPage === page ? '#667eea' : 'transparent',
            color: 'white',
            border: '1px solid #667eea',
            padding: '8px 16px',
            borderRadius: '6px',
            cursor: 'pointer'
          }}
        >
          {page}
        </button>
      ))}
    </nav>
  </div>
);

// Simple demo pages for Support and Insights
const SupportPage = () => (
  <div style={{ padding: '40px' }}>
    <h1>🤝 Peer Support Network</h1>
    <p>Anonymous peer matching and support groups (Demo interface)</p>
  </div>
);

const InsightsPage = () => (
  <div style={{ padding: '40px' }}>
    <h1>📊 Campus Insights</h1>
    <p>Administrative dashboard for campus mental health trends (Demo interface)</p>
  </div>
);

function App() {
  const [isLoading, setIsLoading] = useState(true);
  const [currentPage, setCurrentPage] = useState('Dashboard');

  useEffect(() => {
    const timer = setTimeout(() => setIsLoading(false), 2000);
    return () => clearTimeout(timer);
  }, []);

  if (isLoading) {
    return (
      <div style={{
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        minHeight: '100vh',
        background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
        color: 'white',
        flexDirection: 'column'
      }}>
        <div style={{
          border: '3px solid rgba(255, 255, 255, 0.3)',
          borderTop: '3px solid white',
          borderRadius: '50%',
          width: '40px',
          height: '40px',
          animation: 'spin 1s linear infinite',
          marginBottom: '20px'
        }}></div>
        <h1>🧠 MindfulCampus</h1>
        <p>Loading your mental wellness platform...</p>
        <style>{`@keyframes spin { 0% { transform: rotate(0deg); } 100% { transform: rotate(360deg); } }`}</style>
      </div>
    );
  }

  const renderPage = () => {
    switch(currentPage) {
      case 'Analysis': return <EmotionalAnalysis />;
      case 'Support': return <SupportPage />;
      case 'Insights': return <InsightsPage />;
      default: return <Dashboard />;
    }
  };

  return (
    <div style={{ 
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #0a0e1a 0%, #1a1d29 100%)',
      color: 'white'
    }}>
      <Navigation currentPage={currentPage} setCurrentPage={setCurrentPage} />
      {renderPage()}
    </div>
  );
}

export default App;
